def Syracuse(u):
    if u % 2 == 0:
        return u // 2
    else:
        return 3 * u + 1
    return u

def Liste_Syracuse(u):
    L = [u]
    while u != 1:
        L.append(Syracuse(u))
        u = Syracuse(u)
    print(L)
    return L

def tempsdevol(u):
    temps = 0
    while u != 1:
        u= Syracuse(u)
        temps = temps + 1
    return temps

def min_u0():
    u0 = 1
    while True:
        temps = temp(u0)
        if temps >= 100:
            print(f"La plus petite valeur de u0 est {u0} avec un temps de vol de {temps}") 
            return u0, temps
        u0 = u0 + 1
min_u0()
